'use strict';

var Tesseract = require('tesseract.js');
var vueFragment = require('vue-fragment');

Vue.component("tesseract", {
  template: "\n    <fragment>\n      <slot :oem=\"OEM\" :psm=\"PSM\" :$recognize=\"recognize\" :$createworker=\"createWorker\" :$createscheduler=\"createScheduler\" :$setlogging=\"setLogging\" :$detect=\"detect\">\n      </slot>\n    </fragment>\n  ",
  name: 'TesseractComponent',
  components: {
    'fragment': vueFragment.Fragment
  },
  data: function data() {
    return {
      OEM: Tesseract.OEM,
      PSM: Tesseract.PSM
    };
  },
  methods: {
    recognize: Tesseract.recognize,
    createWorker: Tesseract.createWorker,
    createScheduler: Tesseract.createScheduler,
    setLogging: Tesseract.setLogging,
    detect: Tesseract.detect
  }
});

var TesseractPlugin = {
  install: function install(Vue) {
    /** USE AS A PLUGIN IN VUEJS 2 OR 3 */
    Vue.prototype.$tesseractor = function () {
      return Tesseract;
    };
  }
};

exports.TesseractPlugin = TesseractPlugin;
